mapper.select:
select

from
	sd_order_servicepoint_receivable

	order_id = #{orderId}

		and service_point_id = #{servicePointId}


		and item_no = #{itemNo}

	and quarter = #{quarter}

order by service_point_id,item_no

select

from
sd_order_servicepoint_receivable

	order_id = #{orderId}
	and item_no = #{itemNo}
	and quarter = #{quarter}

mapper.update:
update
	sd_order_servicepoint_receivable

	amount = #{amount},

		form_no = #{formNo},


		remark = #{remark},

	update_by = #{updateBy},
	update_at = #{updateAt}

where
	order_id = #{orderId}
	and service_point_id = #{servicePointId}
	and item_no = #{itemNo}
	and quarter = #{quarter}

update
	sd_order_servicepoint_receivable

	del_flag = #{delFlag},
	update_by = #{updateBy},
	update_at = #{updateAt}

where
	order_id = #{orderId}
	and service_point_id = #{servicePointId}
	and item_no = #{itemNo}
	and quarter = #{quarter}
