mapper.update:
UPDATE fi_customerinvoice SET
	update_by = #{updateBy.id},
	update_date = #{updateDate}, 
	remarks = #{remarks}
WHERE id = #{id}

UPDATE fi_customerinvoice SET
	del_flag = #{DEL_FLAG_DELETE},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}

mapper.select:
SELECT id, order_id, order_no, customer_id as 'customer.id', service_charge, express_charge, travel_charge, material_charge, other_charge, service_times, payment_type, status, invoice_date, create_by, create_date, remarks, quarter FROM fi_customerinvoice
AND order_id=#{orderId}
AND del_flag = 0
